mapper.select:
SELECT a.*, b.status AS user_job_status,u.name AS createUser, updateUser.name AS lastUpdateName 
FROM cahg_job a 
INNER JOIN sys_user u on a.create_user_id = u.user_id
LEFT JOIN sys_user updateUser on a.create_user_id = updateUser.user_id
INNER JOIN cahg_job_flow b on a.job_id = b.job_id 
WHERE b.user_id = #{userId} 
GROUP BY b.job_id

 	 and b.`status`= #{userJobStatus}

ORDER BY b.status, a.create_user_date DESC

	limit #{offset}, #{limit}

SELECT job.*,u.name AS createUser, updateUser.name AS lastUpdateName FROM cahg_job job INNER JOIN sys_user u on job.create_user_id = u.user_id LEFT JOIN sys_user updateUser on job.last_update_user_id = updateUser.user_id WHERE 1 = 1
AND job.status = #{status}
ORDER BY create_user_date DESC
limit #{offset}, #{limit}


	 	select a.*,(select b.name from sys_user b where b.user_id=a.create_user_id) as flowUserName,
	 	(select c.name from sys_user c where c.user_id=a.flow_user_id) as nextUserName
 		   from cahg_job_detail a where a.job_id=#{jobId}

 	select r.*,d.name as deptName from cahg_job_result r
 	INNER JOIN sys_dept d on r.dept_id = d.dept_id
 	where 1 = 1 

 		and r.job_id = #{jobId}

 	ORDER BY r.create_time DESC

		limit #{offset}, #{limit}

mapper.update:
update cahg_job_flow 

   `status` = #{status} 

where job_id = #{jobId} and user_id = #{userId}
